import React from "react";
import { api } from "../../utils/api";

function Main({ onEditAvatar, onEditProfile, onAddPlace }) {
  const [userName, setUserName] = React.useState("");
  const [userDescription, setUserDescription] = React.useState("");
  const [userAvatar, setUserAvatar] = React.useState("");
  const [cards, setCards] = React.useState([]);

  React.useEffect(() => {
    api.getUserInfo()
      .then((res) => {
        setUserName(res.name);
        setUserDescription(res.about);
        setUserAvatar(res.avatar);
      });
  });

  React.useEffect(() => {
    api.getInitialCards()
      .then((res) => {
        // setCards(res.map((card) => {
        //   <article className="element" key={card._id}>
        //     <button className="element__delete-icon-btn" aria-label="Удалить" type="button" />
        //     <img src={card.link} alt={card.name} className="element__image" />
        //     <div className="element__title-zone">
        //       <h2 className="element__title">{card.name}</h2>
        //       <div className="element__like-zone">
        //         <button className="element__like-icon-btn" aria-label="Нравится" type="button" />
        //         <p className="element__number-like">{card.likes.length}</p>
        //       </div>
        //     </div>
        //   </article>
        // }))
      });
  })

  return (
    <>
      <main className="content">
        <section className="profile">
          <div className="profile__avatar">
            <button className="profile__avatar-btn" type="button" aria-label="Редактировать" onClick={onEditAvatar} />
            <img src={userAvatar} className="profile__avatar-image" alt="Фото пользователя" />
          </div>
          <div className="profile__info">
            <h1 className="profile__title">{userName}</h1>
            <button className="profile__edit-btn" type="button" aria-label="Редактировать" onClick={onEditProfile} />
            <p className="profile__subtitle">{userDescription}</p>
          </div>
          <button className="profile__add-btn" type="button" aria-label="Создать" onClick={onAddPlace} />
        </section>
        <section className="elements">{cards}</section>
      </main>
    </>
  );
}

export default Main;
