import React from "react";

function WindowWithForm({ title, text, children, onAuthUser }) {
  // const children = [];
  const inputs = [children[0], children[1]];
  const link = children[1];
  const [email, setEmail] = React.useState('');
  const [password, setPassword] = React.useState('');

  function handleInputNameEmail(evt) {
    setEmail(evt.target.value);
  };

  function handleInputNamePassword(evt) {
    setPassword(evt.target.value);
  };

  function handleSubmit(evt) {
    evt.preventDefault();
    onAuthUser({ email, password });
  };

  return (
    <div className="window">
      <h2 className="window__title">{title}</h2>
      <form className="window__form" onSubmit={handleSubmit}>
        {inputs}
      </form>
      <button className="window__button" type="submit">{text}</button>
      {link}
    </div>
  );
}

export default WindowWithForm;
