import React from "react";
import { api } from "../../utils/api";
import Card from "../Card/Card";

function Main({ onEditAvatar, onEditProfile, onAddPlace, onCardClick }) {
  const [userName, setUserName] = React.useState("");
  const [userDescription, setUserDescription] = React.useState("");
  const [userAvatar, setUserAvatar] = React.useState("");
  const [cards, setCards] = React.useState([]);

  React.useEffect(() => {
    api.getUserInfo()
      .then((res) => {
        setUserName(res.name);
        setUserDescription(res.about);
        setUserAvatar(res.avatar);
      })
      .catch((err) => {
        console.log(err);
      })

  }, []);

  React.useEffect(() => {
    api.getInitialCards()
      .then((res) => {
        setCards(res);
      })
      .catch((err) => {
        console.log(err);
      })
  }, []);

  return (
    <main className="content">
      <section className="profile">
        <div className="profile__avatar">
          <button className="profile__avatar-btn" type="button" aria-label="Редактировать" onClick={onEditAvatar} />
          <img src={userAvatar} className="profile__avatar-image" alt="Фото пользователя" />
        </div>
        <div className="profile__info">
          <h1 className="profile__title">{userName}</h1>
          <button className="profile__edit-btn" type="button" aria-label="Редактировать" onClick={onEditProfile} />
          <p className="profile__subtitle">{userDescription}</p>
        </div>
        <button className="profile__add-btn" type="button" aria-label="Создать" onClick={onAddPlace} />
      </section>
      <section className="elements">{cards.map((card) => (<Card card={card} key={card._id} onCardClick={onCardClick} />))}</section>
    </main>
  );
}

export default Main;
